<style scoped lang="css">
.text {
  width: 80vw;
  margin: auto;
}

.sparkle-one,
.sparkle-two {
  position: absolute;
  width: 20vw;
  min-width: var(--space-8);
}

.sparkle-one {
  top: var(--space-1);
  right: var(--space-1);
}

.sparkle-two {
  bottom: var(--space-1);
  left: var(--space-1);
}

.black-line {
  fill: none;
  stroke: black;
  stroke-width: 10;
  stroke-linecap: round;
  stroke-linejoin: round;

  stroke-dasharray: 400%;
  /* stroke-dashoffset: 1000; */
  animation: draw_stroke 10s linear;
  animation-fill-mode: forwards;
}

@keyframes draw_stroke {
  from {
    stroke-dashoffset: 400%;
  }

  to {
    stroke-dashoffset: 0%;
  }
}
</style>